css:
body {
      font-family: Arial, sans-serif;
      line-height: 1.6;
      margin: 20px;
    }
    table {
      border-collapse: collapse;
      width: 100%;
      font-size: 13px;
    }
    th, td {
      border: 1px solid #ddd;
      padding: 8px;
      text-align: left;
    }
    th {
      background-color: #f2f2f2;
    }

.scrollable-table {
    display: flex;
      align-items: center;
      justify-content: center;
}

.scrollable-table th{
    text-align: center;
    vertical-align: middle !important;
}
